  { :reqTitle => "Result Entry via ACII File or Instrument Interface", :reqText => "Result entry may be done by use of intelligent instrument interfaces or multi-sample/multi-test ASCII files.", :created_at => nil, :updated_at => "2012-10-04 03:14:28", :category_id => 9, :status => "Public", :source_id => nil, :sortOrder => nil },
