  { :reqTitle => "Worksheet", :reqText => "Describe how a laboratory analyst enters data into an electronic worksheet of test measurements and results.", :created_at => "2013-01-25 18:09:39", :updated_at => "2013-01-25 18:09:39", :category_id => 9, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Transfer Test Items ", :reqText => "Describe how your system electronically transfers an item during testing from one functional area to another.", :created_at => "2013-01-25 18:10:59", :updated_at => "2013-01-25 18:10:59", :category_id => 9, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Entry & Accuracy ", :reqText => "Describe how test results are recorded and how their accuracy is ensured.", :created_at => "2013-01-25 18:11:59", :updated_at => "2013-01-25 18:11:59", :category_id => 9, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Auto/Semi-Auto ", :reqText => "The system must support automated or semi-automated data insertion.", :created_at => "2013-01-25 18:12:58", :updated_at => "2013-01-25 18:12:58", :category_id => 9, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Preliminary vs Final Data ", :reqText => "Released preliminary data are clearly differentiated from fully validated results.", :created_at => "2013-01-28 14:15:55", :updated_at => "2013-01-28 14:15:55", :category_id => 9, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "QA/QC ", :reqText => "Provide the functionality in the product to fully manage all aspects of laboratory quality control. This will include the ability to create QA/QC tests and Blanks, Spikes, Duplicate, Precision and Recovery and attach calculations to those test codes as well as report and chart all quality control data captured in the lab.", :created_at => "2013-01-28 14:16:57", :updated_at => "2013-01-28 14:16:57", :category_id => 9, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "QA/QC Charts ", :reqText => "Provide a full statistics package and be capable of creating QA/QC charts for all recovery, precision and lab control samples.", :created_at => "2013-01-28 14:17:42", :updated_at => "2013-01-28 14:17:42", :category_id => 9, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Audit Trail ", :reqText => "Audit trail and specifications violation trails must be maintained. Audit trail must include user, date/time and a required reason for change as a standard part of the application. The Audit trail must include result changes as well as header information changes.", :created_at => "2013-01-28 14:18:40", :updated_at => "2013-01-28 14:18:40", :category_id => 9, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Multiple Dilutions ", :reqText => "When running samples for multiple dilutions such as GCMS, the user may review onscreen the stored result, the diluted result with corrected MDL’s, and qualifiers. The user may select the entire old values, the entire new values or any combination of the two for final reporting.", :created_at => "2013-01-28 14:20:18", :updated_at => "2013-01-28 14:20:18", :category_id => 9, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Validation/Approval before Report", :reqText => "Approval must occur before a sample can be placed in a report queue.", :created_at => "2013-01-28 14:21:06", :updated_at => "2013-01-28 14:21:06", :category_id => 9, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Menu-driven Approval ", :reqText => "Results validation must be a menu-driven process.", :created_at => "2013-01-28 14:22:19", :updated_at => "2013-01-29 14:12:29", :category_id => 9, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Comments Review ", :reqText => "Results validation must allow review of any comments or special information about the sample.", :created_at => "2013-01-28 14:22:58", :updated_at => "2013-01-28 14:22:58", :category_id => 9, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "QA ", :reqText => "Describe how your system can provide the basis for a quality assurance program, including proficiency testing, scheduled maintenance on equipment, etc.", :created_at => "2013-01-28 14:24:11", :updated_at => "2013-01-28 14:24:11", :category_id => 9, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Monthly Percentages ", :reqText => "Must be able to provide monthly QA/QC percentages for testing.", :created_at => "2013-01-28 14:24:53", :updated_at => "2013-01-28 14:24:53", :category_id => 9, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "User Friendly ", :reqText => "The system must allow authorized users to change data in user-friendly manner.", :created_at => "2013-01-28 14:25:55", :updated_at => "2013-01-28 14:25:55", :category_id => 9, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Approve Data ", :reqText => "The system must possess the capability of validating/approving data prior to data being moved to main database.", :created_at => "2013-01-28 14:27:14", :updated_at => "2013-01-28 14:27:14", :category_id => 9, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Data Field Additions", :reqText => "The LIMS must be capable of adding fields without recourse to the vendor for support.", :created_at => nil, :updated_at => "2012-10-05 04:29:19", :category_id => 7, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Customized Result Entry Screens", :reqText => "Result entry must support the use of customized screens supporting special information specific to that analysis. These screens are user definable and allow such items as Chromatographic conditions.", :created_at => nil, :updated_at => "2012-10-05 04:32:19", :category_id => 7, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Sample Metadata Fields", :reqText => "The system must allow the user to add universal fields to the database for required data on all samples logged into the system. These fields must be able to be added at any time during or after implementation. Addition database fields must not void the Warranty nor require vendor review at a later date.  Metadata fields must be able to hold and present all manner of textual data, including hyperlinks, as well as things like links to other pieces of external data.", :created_at => nil, :updated_at => "2012-10-10 02:23:32", :category_id => 7, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Custom Screens, Apps, and Reports", :reqText => "Allows custom screens, apps, and reports by System Administrator.", :created_at => nil, :updated_at => "2012-10-05 04:41:20", :category_id => 7, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Dynamic Caption Changes", :reqText => "The system provides the ability to dynamically change captions (labels) for fields on screens in the system.", :created_at => nil, :updated_at => "2012-10-05 04:42:43", :category_id => 7, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Ease of Modification", :reqText => "The system can be easily and efficiently modified to meet lab growth and changing business needs.", :created_at => nil, :updated_at => "2012-10-05 04:45:28", :category_id => 7, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Ease of Upgrade", :reqText => "The system must be designed so that routine upgrades and maintenance do not require reconfiguration of the system.", :created_at => "2012-10-05 04:30:18", :updated_at => "2012-10-05 04:30:18", :category_id => 7, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Custom Sample ID", :reqText => "The system must permit the use of a custom sample identification system.", :created_at => "2012-10-10 02:26:56", :updated_at => "2012-10-10 02:26:56", :category_id => 7, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Role Based Security ", :reqText => "Describe how your system provides role-based user access, no/limited/full access that is configurable.", :created_at => "2013-01-25 14:30:36", :updated_at => "2013-01-25 14:30:36", :category_id => 7, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Dynamically Configurable ", :reqText => "The system must have dynamically configurable limit periods and notification hierarchy.", :created_at => "2013-01-25 14:31:38", :updated_at => "2013-01-25 14:31:38", :category_id => 7, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Graphics ", :reqText => "Custom graphic forms generation must be included.", :created_at => "2013-01-25 14:33:07", :updated_at => "2013-01-25 14:33:07", :category_id => 7, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "User Management ", :reqText => "The system must include System Administration ability to create/modify/duplicate users.", :created_at => "2013-01-25 14:39:31", :updated_at => "2013-01-25 14:39:31", :category_id => 7, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Password Management ", :reqText => "The system must include System Administration ability to reset user passwords.", :created_at => "2013-01-25 14:41:46", :updated_at => "2013-01-25 14:41:46", :category_id => 7, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Workflow Matching ", :reqText => "Conduct an evaluation of the laboratories and configure the LIMS to the characteristics of the laboratory.", :created_at => "2013-01-25 14:42:42", :updated_at => "2013-01-25 14:42:42", :category_id => 7, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Additional Equipment ", :reqText => "The system includes the ability to integrate additional printers and scanners.", :created_at => "2013-01-25 14:44:14", :updated_at => "2013-01-25 14:44:14", :category_id => 7, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Sampling Cost Reports ", :reqText => "The system must be capable of generating sampling cost reports for each industry, which includes the number of visits, analysis, calculate any administrative cost, tubing cost, and duplicate sample cost, etc.", :created_at => "2013-01-28 20:21:56", :updated_at => "2013-01-28 20:21:56", :category_id => 17, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Sample Point Data Dictionary ", :reqText => "The system must support a flexible procedure to assign location codes or sample types for each default sample point. This includes tests required, report types and invoice information (where required), maximum holding time, project accounts and location description. Each location code must have an upper and lower warning limit and target specification for every analyte assigned. \r\n\r\nThese specifications must be checked each time a sample result for that location is entered or validated. Out-of-limits should be color coded on the screen and be easily noted visually immediately upon entry. These same specifications must be used to automatically generate Quality Control Charts and Reports. In addition, each location code must have stored defaults for all user-defined universal fields, special project fields, and sample comments.\r\n", :created_at => "2013-01-28 20:23:32", :updated_at => "2013-01-28 20:23:32", :category_id => 17, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Surcharge Fees ", :reqText => "The system must be able to calculate Surcharge Fees for each industry.", :created_at => "2013-01-28 20:24:23", :updated_at => "2013-01-28 20:24:23", :category_id => 17, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Cost Data ", :reqText => "The proposed system must be capable of compiling cost data for each industry in the database.", :created_at => "2013-01-28 20:25:28", :updated_at => "2013-01-28 20:25:28", :category_id => 17, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Permits ", :reqText => "The proposed system must be capable of storing permits for each industry.", :created_at => "2013-01-28 20:26:35", :updated_at => "2013-01-28 20:26:35", :category_id => 17, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Link Criteria ", :reqText => "The system must be capable of linking permit criteria with Significantly Non-Compliant criteria.", :created_at => "2013-01-28 20:27:40", :updated_at => "2013-01-28 20:27:40", :category_id => 17, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Local Limits ", :reqText => "The system must be capable of generating notices of violation according to permit criteria and local limits.", :created_at => "2013-01-28 20:28:41", :updated_at => "2013-01-28 20:28:41", :category_id => 17, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Pretreatment ", :reqText => "The system must be able to store standard operating procedures (SOPs) for all pretreatment procedures.", :created_at => "2013-01-28 20:29:22", :updated_at => "2013-01-28 20:29:22", :category_id => 17, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Methods ", :reqText => "The proposed system must be capable of linking all lab results with the correct reporting test method such as Standard Methods, EPA method 624.", :created_at => "2013-01-28 20:31:13", :updated_at => "2013-01-28 20:31:13", :category_id => 17, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Permit Letters ", :reqText => "The system must be capable of generating correspondence letter for permit renewals and new permits.", :created_at => "2013-01-28 20:32:38", :updated_at => "2013-01-28 20:32:38", :category_id => 17, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Report Templates ", :reqText => "The proposed system must be capable of generating the following Report templates: permit, permit application, directions for the application, and semi annual report. \r\n\r\nThe report templates must have the following information: Name of industry, mailing address, SIC Code, permit number, contact name.\r\n", :created_at => "2013-01-28 20:34:52", :updated_at => "2013-01-28 20:34:52", :category_id => 17, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Citation Reminders ", :reqText => "The LIMS must be capable of generating reminder notices for citation dates.", :created_at => "2013-01-28 20:35:42", :updated_at => "2013-01-28 20:35:42", :category_id => 17, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "SIC Codes ", :reqText => "All Industries/companies must be able to be indexed by SIC Codes.", :created_at => "2013-01-28 20:36:45", :updated_at => "2013-01-28 20:36:45", :category_id => 17, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Indexing", :reqText => "All industries must be able to be indexed by categorical and non-categorical, exempt and non-exempt, alphabetical order, permit number, bar code, permit due dates, and be daily, weekly, biweekly, monthly, quarterly, bi-annual and annual.", :created_at => "2013-01-28 20:37:52", :updated_at => "2013-01-28 20:37:52", :category_id => 17, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Address Book ", :reqText => "The system must be able to have an address book for all programs in Environmental Compliance. Indexing alphabetical, industry, address, etc.", :created_at => "2013-01-28 20:39:05", :updated_at => "2013-01-28 20:39:05", :category_id => 17, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Violations/Noncompliance ", :reqText => "The system must be able to index and compile lists of industries that are in noncompliance/violation.", :created_at => "2013-01-28 20:40:03", :updated_at => "2013-01-28 20:40:03", :category_id => 17, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Calculate Surcharge ", :reqText => "The system must be able to calculate surcharge from Chain of Custody’s and Data Results.", :created_at => "2013-01-28 20:40:49", :updated_at => "2013-01-28 20:40:49", :category_id => 17, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Oil & Grease ", :reqText => "The system must be capable of entering matrix duplicates for oil and grease samples.", :created_at => "2013-01-28 20:41:38", :updated_at => "2013-01-28 20:41:38", :category_id => 17, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Formula/Limits ", :reqText => "The system must be capable of determining violation by either combined waste stream formula or local limits.", :created_at => "2013-01-28 20:42:28", :updated_at => "2013-01-28 20:42:28", :category_id => 17, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Permits ", :reqText => "The system must be capable of assigning permit numbers and permit expiration.", :created_at => "2013-01-28 20:43:24", :updated_at => "2013-01-28 20:43:24", :category_id => 17, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Waste Stream Formula ", :reqText => "Must be capable of calculating combined waste stream formula (which is found in Ordinance 8140 and 40 CFR part 403.6, section e) from information provided through data entry.", :created_at => "2013-01-28 20:44:22", :updated_at => "2013-01-28 20:44:22", :category_id => 17, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "SNC ", :reqText => "The system must be capable of generating a report that determines if an industry is in significant non-compliance (SNC).", :created_at => "2013-01-28 20:47:29", :updated_at => "2013-01-28 20:47:29", :category_id => 17, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "BOD/TSS ", :reqText => "The system must generate a BOD and TSS result report for industries.", :created_at => "2013-01-28 20:48:33", :updated_at => "2013-01-28 20:48:33", :category_id => 17, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Report Types ", :reqText => "The system must be capable of generating and printing industrial reports, sampling, inspections, fact sheets, Nov’s/Citations/Formal Charges, correspondence letters and industrial permits.", :created_at => "2013-01-28 20:49:38", :updated_at => "2013-01-28 20:49:38", :category_id => 17, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Special Reports ", :reqText => "The system must generate a special report for HAZMAT Incidents, storm water violations, Spills/Slugs, and local limit violators.", :created_at => "2013-01-28 20:50:43", :updated_at => "2013-01-28 20:50:43", :category_id => 17, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "CROMERR (40CFR 3)", :reqText => "The system must be compliant with the Cross Media Electronic Reporting Rule (CROMERR Part III of Title 40 CFR).", :created_at => "2013-01-28 20:53:06", :updated_at => "2013-01-28 20:53:06", :category_id => 17, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Correspondence", :reqText => "The system must be able to generate corresponding letters/templates for special and routine cases (ex: industrial waste, permit renewals, semiannual reports).", :created_at => "2013-01-28 20:54:28", :updated_at => "2013-01-28 20:54:28", :category_id => 17, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Evaluation Availability", :reqText => "The solution must be available for full demonstration, preferably online, and the provider must be able to conduct an onsite demonstration. Explain your ability to meet these conditions.", :created_at => "2012-10-02 02:26:14", :updated_at => "2012-11-22 00:14:19", :category_id => 1, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Ad Hoc Query Output", :reqText => "Output from ad hoc queries is able to be directed to a computer file for subsequent analysis by other software.", :created_at => nil, :updated_at => "2012-10-05 04:52:53", :category_id => 11, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "NAIS Data Exchange", :reqText => "The system must have the ability to exchange data with National Identification System (NAIS) tracking systems.", :created_at => nil, :updated_at => "2012-10-05 04:59:32", :category_id => 11, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "EDD Formats", :reqText => "The system must provide a library of common and/or basic electronic data deliverable (EDD) formats.", :created_at => nil, :updated_at => "2012-10-05 05:04:10", :category_id => 11, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Word and Excel Interface", :reqText => "The system imports/exports data from/to MS Word or Excel.", :created_at => nil, :updated_at => "2012-10-05 05:05:37", :category_id => 11, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Integration with Exchange", :reqText => "The LIMS can be integrated with MS Exchange services.", :created_at => nil, :updated_at => "2012-10-05 05:06:11", :category_id => 11, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Contract Laboratory Interface", :reqText => "The system must be able to interface with a Contract Laboratory to retrieve datasheets, data analysis and any other information pertaining to specific samples or specimens.", :created_at => nil, :updated_at => "2012-10-05 05:10:09", :category_id => 11, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Electronic Laboratory Notebook", :reqText => "The system must be capable of integrating with an electronic laboratory notebook(ELN) system.  Alternatively, the system may provide ELN functionality internal to the system.", :created_at => "2012-10-10 02:49:57", :updated_at => "2012-10-10 02:49:57", :category_id => 11, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Mobile Device Integration", :reqText => "The system provides thorough support for mobile devices, especially tablets and smart phones.", :created_at => "2012-10-10 03:34:18", :updated_at => "2012-10-10 03:34:18", :category_id => 11, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Transfer Data ", :reqText => "Describe how your system can transfer data to and from another Record Management System.", :created_at => "2013-01-25 18:18:00", :updated_at => "2013-01-25 18:18:00", :category_id => 11, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Mobile Device Docking ", :reqText => "The system must support the docking of mobile devices.", :created_at => "2013-01-25 18:19:05", :updated_at => "2013-01-25 18:19:05", :category_id => 11, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Contracted Sample Analysis Retrieval ", :reqText => "The system must be able to electronically retrieve sample analysis from Contract Labs.", :created_at => "2013-01-25 18:21:45", :updated_at => "2013-01-25 18:21:45", :category_id => 11, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Program Interfacing ", :reqText => "Must be capable of interfacing with multiple programs (eg: Excel, Word, Access, other standard applications).\r\n", :created_at => "2013-01-25 18:24:03", :updated_at => "2013-01-25 18:24:03", :category_id => 11, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Evidence Transfer ", :reqText => "Describe how certified lab evidence transfer meets ASCLD standards.", :created_at => "2013-01-28 19:01:02", :updated_at => "2013-01-28 19:01:02", :category_id => 25, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "ASTM", :reqText => "Describe how the system supports ASTM standards. Include evidence handling and numbering.", :created_at => "2013-01-28 19:03:48", :updated_at => "2013-01-28 19:03:48", :category_id => 25, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Documents & Fingerprints", :reqText => "Analysis of document and fingerprint evidence is supported.", :created_at => "2013-01-28 19:05:32", :updated_at => "2013-01-28 19:05:32", :category_id => 25, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Review ", :reqText => "Case notes, data, and reports are previewed before approval.", :created_at => "2013-01-28 19:11:10", :updated_at => "2013-01-28 19:11:10", :category_id => 25, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Track Property/Evidence ", :reqText => "Describe how your system uses barcoding technology to identify and track the property/evidence.", :created_at => "2013-01-28 19:12:53", :updated_at => "2013-01-28 19:12:53", :category_id => 25, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Sundry Functionality ", :reqText => "Describe how your system provides functionality for each discipline, including latent prints, crime scene unit, multi-media lab, evidence control, ballistics, polygraph, forensic chemistry, and DNA.", :created_at => "2013-01-28 19:13:51", :updated_at => "2013-01-28 19:13:51", :category_id => 25, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Case Management ", :reqText => "Describe how your system internally manages case workflow, including case assignment, case forwarding and notification.", :created_at => "2013-01-28 19:14:52", :updated_at => "2013-01-28 19:14:52", :category_id => 25, :status => "Public", :source_id => nil, :sortOrder => nil },
