  { :reqTitle => "NELAP Chain of Custody", :reqText => "The system must provide a user selectable NELAP-compliant internal Chain of Custody that tracks all samples and all containers associated with samples from the time they are collected until disposal.", :created_at => nil, :updated_at => "2012-11-22 05:12:06", :category_id => 5, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Data Manipulation Audit Trail", :reqText => "System must maintain an audit trail of all data manipulation consistent with applicable regulations and standards.", :created_at => nil, :updated_at => "2012-10-06 06:02:33", :category_id => 5, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "NELAP/TNI Standards", :reqText => "The system must meet NELAC/NELAP/TNI standards.", :created_at => nil, :updated_at => "2012-10-06 06:03:08", :category_id => 5, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Classified Information Handling", :reqText => "The system must meet the Government requirements for handling classified information and documents.", :created_at => nil, :updated_at => "2012-10-06 06:04:41", :category_id => 5, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Automatic Date and Time Stamps", :reqText => "The system must include automatic date and time stamping.", :created_at => nil, :updated_at => "2012-10-06 06:05:52", :category_id => 5, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Electronic/Digital Signature Support", :reqText => "The system must provide the ability to insert/manage secure electronic and/or digital signatures.", :created_at => nil, :updated_at => "2012-10-06 06:09:20", :category_id => 5, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "ISO 17025 Support", :reqText => "The system must support ISO 17025.", :created_at => nil, :updated_at => "2012-10-06 06:11:14", :category_id => 5, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "21 CFR Part 11 Compliance", :reqText => "The system must support compliance with 21 CFR part 11.", :created_at => nil, :updated_at => "2012-11-22 05:13:08", :category_id => 5, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "40 CFR part 3 Compliance", :reqText => "The system must support compliance with 40 CFR part 3.", :created_at => nil, :updated_at => "2012-11-22 05:14:20", :category_id => 5, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "HIPAA Compliance", :reqText => "The system must support compliance with HIPAA.", :created_at => nil, :updated_at => "2012-11-22 05:14:50", :category_id => 5, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Environmental Monitoring", :reqText => "The system must provide a means of capturing data regarding the environmental conditions of the testing laboratory, as well as the storage environment.", :created_at => "2012-10-10 03:27:56", :updated_at => "2012-10-10 03:27:56", :category_id => 5, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "GALP/GAMP ", :reqText => "Describe how the system supports GALP/GAMP standards.", :created_at => "2013-01-24 15:08:47", :updated_at => "2013-01-24 15:08:47", :category_id => 5, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Accession Support File Upload", :reqText => "The system supports pictures and documents upload in standard formats for attachment to accessions.", :created_at => nil, :updated_at => "2012-10-06 05:34:11", :category_id => 8, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "System Supports Billable and Non-Billable Tests", :reqText => "The system allows users to handle billable and non-billable tests on the same accession.", :created_at => nil, :updated_at => "2012-10-06 05:34:54", :category_id => 8, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Shipping and Receiving Tracking", :reqText => "The system supports tracking of shipping and receiving.", :created_at => nil, :updated_at => "2012-10-06 05:36:02", :category_id => 8, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Routing, Custody, and Work Sheets", :reqText => "At login, the system must be able to print user definable chain of custodies, worksheets, routing sheets and custom labels.", :created_at => nil, :updated_at => "2012-10-06 05:44:26", :category_id => 8, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Sample Login Groups", :reqText => "Pre-login groups for routine samples must be easily created, saved, and recalled for recurring logins.", :created_at => nil, :updated_at => "2012-10-06 05:45:52", :category_id => 8, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Sample Scheduler/Tracker", :reqText => "An Ad-Hoc Sample Request/Scheduler must include a comprehensive sample scheduler, tracker and sample flow management system.", :created_at => nil, :updated_at => "2013-01-25 15:08:39", :category_id => 8, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Recurring Login", :reqText => "System must offer streamlined login of recurring sampling projects.", :created_at => nil, :updated_at => "2012-10-06 05:49:21", :category_id => 8, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Monthly Sampling Schedule", :reqText => "The system must be capable of generating a monthly sampling schedule from a schedule database.", :created_at => nil, :updated_at => "2012-10-06 05:56:21", :category_id => 8, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Weekly Sample Labels", :reqText => "The system must be able to generate labels for samples being tested each week.", :created_at => nil, :updated_at => "2012-10-06 05:59:54", :category_id => 8, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Sample Receipts", :reqText => "The system can produce electronic as well as paper receipts for received samples.", :created_at => "2012-10-10 02:32:00", :updated_at => "2012-10-10 02:32:00", :category_id => 8, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Accession Records ", :reqText => "The system must create and maintain an electronic Accession record set for each accession received.", :created_at => "2013-01-25 14:46:04", :updated_at => "2013-01-25 14:46:04", :category_id => 8, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Online Forms ", :reqText => "The system includes the ability to receive accession information from web-enabled forms.", :created_at => "2013-01-25 15:01:47", :updated_at => "2013-01-25 15:01:47", :category_id => 8, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Upload Data ", :reqText => "The system must be compatible with automated uploading of field-derived sample collection data.", :created_at => "2013-01-25 15:03:19", :updated_at => "2013-01-25 15:03:19", :category_id => 8, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Multiple Sample Logins ", :reqText => "The system must support a flexible user configurable spreadsheet style multi-sample login. Multiple single samples as a group or automated non-editable groups are not acceptable. The spreadsheet must be user configurable and changeable without programming. The system must support multiple login templates from which to choose, depending on sample type. The multi login feature must support the modification of each sample or multiple samples for tests, sample comments, sample special information all at the same time prior to actual login without having to login a group of samples and then modify them later. At login, each sample may be assigned tests, qualified technicians, calibrated instruments, and costing if desired. The multi sample login must permit the selection of samples to be logged in multiple ways. The samples may be selected from the sample prescheduler by month, day, hour, etc., from the optional portable data entry terminal, from the optional portable georeferenced sample data logger, or from user saved pre-login groups. All this must be done without programming. The login must support ad hoc samples not predefined in the sample point list. The user may choose to make a one time login or add the ad-hoc sample to the permanent list. At login, the system must generate user configurable paperwork, labels and worksheets can be printed. It must support the triggering of vendor or user defined programs to be run on the samples just prior to or just after login. Custom routines allow the user to customize the system login without requiring vendor programming.", :created_at => "2013-01-25 15:05:26", :updated_at => "2013-01-25 15:05:26", :category_id => 8, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Sample Scheduling ", :reqText => "The LIMS must have a tool to provide a comprehensive view of all samples and projects in the system using a color-coded status view of the current and scheduled samples. \r\n\r\nRoutine repetitive samples must be able to be logged in using predefined groups or automatically with this tool. The scheduler must also alert sample collectors about the need to collect specific samples and maintains sample status and communicates the status and location of samples throughout the organization. \r\n\r\nThe Scheduler is used to schedule routine samples. These can be hourly, daily, weekly or monthly samples. These routine samples are grouped together and can be enabled and disabled as a group. This makes it easy to turn on and off sampling for different production lines or processes as they go on and off line. \r\n\r\nA report must be included with the Scheduler to display Laboratory Performance, and must have statistics detailing laboratory sample turn around time for production to acknowledge sample results. Result entry should be single sample, single test, multi-sample/multi-test, file entry or calculated mode. All must be full screen editing spreadsheet style. All must check specifications against both test and individual sample points. Result entry templates must be easily user configurable without programming.\r\n", :created_at => "2013-01-25 15:11:02", :updated_at => "2013-01-25 15:11:02", :category_id => 8, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Auto Assign ", :reqText => "The system must offer automatic assignment of analysis requests.", :created_at => "2013-01-25 15:13:04", :updated_at => "2013-01-25 15:13:04", :category_id => 8, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Preliminary Results", :reqText => "Released preliminary data are clearly differentiated from fully validated results.", :created_at => nil, :updated_at => "2012-10-09 01:42:48", :category_id => 12, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Results Transmission Limitations", :reqText => "Final report will be released, and electronically transmitted results will not be sent, until all case reviews have been completed in the LIMS by the case coordinator.", :created_at => nil, :updated_at => "2012-11-22 05:59:18", :category_id => 12, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Static Summary Reports", :reqText => "The system provides the ability to automatically post static Summary Reports on internal web server on a periodic basis.", :created_at => nil, :updated_at => "2012-10-09 01:47:12", :category_id => 12, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Ad Hoc Web Reporting", :reqText => "The system provides an ad hoc web reporting interface to report on user-selected criteria.", :created_at => nil, :updated_at => "2012-11-22 05:59:54", :category_id => 12, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Results Export Options", :reqText => "The system provides the capability to export results in a variety of methods including fax, e-mail, print, and publish to web site. Formats should include: RTF, PDF, HTML, DOC, XLS, XML, and TXT.", :created_at => nil, :updated_at => "2012-10-10 03:28:40", :category_id => 12, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Statistics Package", :reqText => "Provide a full statistics package and be capable of creating QA/QC charts for all recovery, precision and lab control samples", :created_at => nil, :updated_at => "2012-10-09 01:51:59", :category_id => 12, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Exception Report", :reqText => "A separate Exception Report must be created automatically for all data that are stored that are out of specification. It must be possible to automatically e-mail this report to any interested party.", :created_at => nil, :updated_at => "2012-11-22 06:04:08", :category_id => 12, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Results History", :reqText => "Each result must be capable of displaying the history of previous results for that analyte for that sample point. The history must be in the form of a tabular report, graphic trend chart and statistical summary.", :created_at => nil, :updated_at => "2012-10-09 02:00:36", :category_id => 12, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Remote Sample Progress Reports", :reqText => "Sample Progress Reports must be available at any terminal or may be shown on remote PCs using remote access. Password security must limit access to data based on application limits as well as sample ownership.", :created_at => nil, :updated_at => "2012-10-09 02:14:53", :category_id => 12, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Approval Gate", :reqText => "Samples results must be approved before being allowed into the report queue.", :created_at => nil, :updated_at => "2012-10-09 02:16:30", :category_id => 12, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Report Writer", :reqText => "A versatile report writer and forms generator must be provided.", :created_at => nil, :updated_at => "2012-10-09 02:18:05", :category_id => 12, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Custom Graphic Forms", :reqText => "Custom graphic forms generation must be included.", :created_at => nil, :updated_at => "2012-10-09 02:18:34", :category_id => 12, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Report Template Generation", :reqText => "Users must be able to develop specific templates for different types of reports.", :created_at => nil, :updated_at => "2012-10-09 02:19:26", :category_id => 12, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Management Reporting", :reqText => "System must be capable of producing Management and Turn Around Time reports and graphs.", :created_at => nil, :updated_at => "2012-10-09 02:21:24", :category_id => 12, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Control Charts", :reqText => "The system must be able to create, and automatically update, control charts.", :created_at => nil, :updated_at => "2012-10-09 02:24:01", :category_id => 12, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Query and Report Support", :reqText => "The system supports queries/reports by fields/keys, status, completion or other variables.", :created_at => nil, :updated_at => "2012-11-22 06:06:00", :category_id => 12, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Template Versions", :reqText => "The system has the ability to maintain template versions and renditions, allowing management and tracking of the template over time.", :created_at => nil, :updated_at => "2012-10-09 02:27:32", :category_id => 12, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "2D Barcode", :reqText => "The system must support 2D barcodes for samples.", :created_at => "2012-10-10 02:38:16", :updated_at => "2012-10-10 02:38:16", :category_id => 12, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Report/Inquiry Tools ", :reqText => "Databases can be accessed with user-friendly report writing and inquiry tools.", :created_at => "2013-01-28 14:37:43", :updated_at => "2013-01-28 14:37:43", :category_id => 12, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Online Reports - Multiple Users ", :reqText => "The system provides the ability to support multiple web browsers when viewing on-line reports.", :created_at => "2013-01-28 14:38:43", :updated_at => "2013-01-28 14:38:43", :category_id => 12, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "BackLog ", :reqText => "The system must include several standard reports and query routines to access all samples with the pending status through a backlog report. Samples must remain in the backlog until all tests assigned to the sample have been completed. The backlog search must be performed at the user’s choice by simple point and click selection of any of the following criteria: All Laboratory, Department, Analysis, Submittal date, Collection date, Prep test complete, location, Project, Sample Delivery group, and other user selectable fields. The report must be user definable with sample point and click tools. It must be possible to issue worksheets from the backlog report. In addition the backlog must be able to produce ASCII, CSV and XLS files compatible with portable devices such as Pocket PC’s supporting Windows CE applications.", :created_at => "2013-01-28 14:40:07", :updated_at => "2013-01-28 14:40:07", :category_id => 12, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Progress Report ", :reqText => "The system must produce a Read Only progress report that allows printed reports of sample status and data collected to date.", :created_at => "2013-01-28 14:40:50", :updated_at => "2013-01-28 14:40:50", :category_id => 12, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Exception Trail and Reporting ", :reqText => "Exception Trail and Reporting must be included for all out-of-specification results entered. The exception reporting must support immediate email of the exception to a designated person or group to ensure the exception is reacted to properly and in a timely manner.", :created_at => "2013-01-28 14:42:50", :updated_at => "2013-01-28 14:42:50", :category_id => 12, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Customizable Formal Reports", :reqText => "The system must be able to produce customized final formal reports.", :created_at => "2013-01-28 14:44:49", :updated_at => "2013-01-28 14:44:49", :category_id => 12, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Report Process ", :reqText => "Describe how your system can generate, store, reproduce and display laboratory reports as needed, including narrative.", :created_at => "2013-01-28 14:46:36", :updated_at => "2013-01-28 14:46:36", :category_id => 12, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Various Reports", :reqText => "Describe how your system generates statistical reports and other reports, such as inventory control, etc.", :created_at => "2013-01-28 14:48:30", :updated_at => "2013-01-28 14:48:30", :category_id => 12, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Query & Print COC", :reqText => "System must allow users to query chain of custody and print.", :created_at => "2013-01-28 14:49:20", :updated_at => "2013-01-28 14:49:20", :category_id => 12, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Permission ", :reqText => "The system prohibits issuing reports outside of qualified areas.", :created_at => "2013-01-28 14:53:36", :updated_at => "2013-01-28 14:53:36", :category_id => 12, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Any Data/Tables ", :reqText => "Reports can be generated from any data in tables by authorized users.", :created_at => "2013-01-28 14:54:18", :updated_at => "2013-01-28 14:54:18", :category_id => 12, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "MS Office ", :reqText => "The system uses MS Office tools for reporting and formatting of reports.", :created_at => "2013-01-28 15:16:58", :updated_at => "2013-01-28 15:16:58", :category_id => 12, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Auto Reports ", :reqText => "The system has the capability to produce automated Laboratory Reports of Findings and other written documents.", :created_at => "2013-01-28 15:22:43", :updated_at => "2013-01-28 15:22:43", :category_id => 12, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Productivity Reports", :reqText => "The system must allow production of automated individual and aggregate workload and productivity reports on all operational and administrative activities.", :created_at => "2013-01-28 15:24:33", :updated_at => "2013-01-28 15:24:33", :category_id => 12, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Semiannual Reports ", :reqText => "The system must be capable of generating template letters for semiannual report.", :created_at => "2013-01-28 15:26:21", :updated_at => "2013-01-28 15:26:21", :category_id => 12, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Statement of Work", :reqText => "The Vendor must supply information about their overall project approach, acknowledgement of the deliverables, time/schedule constraints and any other criteria for the project. A specific plan that includes the items listed is provided in the Project Plan/Schedule subsection.", :created_at => "2013-01-28 21:29:01", :updated_at => "2013-01-28 21:29:01", :category_id => 21, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Traceability Matrix", :reqText => "Vendor must provide a table linking each deliverable to the corresponding User Requirement Specifications it fulfills.", :created_at => "2013-01-28 21:31:44", :updated_at => "2013-01-28 21:31:44", :category_id => 23, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "GUI Requirements", :reqText => "The system provides an intuitive, user-friendly graphical user interface (GUI), consistent with that of Microsoft products (e.g. Internet Explorer, Word, Excel, PowerPoint, and Windows Media Player).", :created_at => nil, :updated_at => "2012-11-22 06:07:50", :category_id => 14, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Navigation Keys", :reqText => "Must have navigation keys to freely move from field to field", :created_at => nil, :updated_at => "2012-10-09 02:30:29", :category_id => 14, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Data Sorting", :reqText => "Users must be able to sort data.", :created_at => nil, :updated_at => "2012-10-09 02:30:49", :category_id => 14, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Sample Status Icons", :reqText => "User interface visual indicator such as status icons to indicate a sample’s status in the workflow.", :created_at => nil, :updated_at => "2012-10-09 02:31:22", :category_id => 14, :status => "Public", :source_id => nil, :sortOrder => nil },
  { :reqTitle => "Out of Specification Result Color", :reqText => "Single sample must give colored visual indication of previously entered data as well as new data. When a result is entered, the cell must immediately change color to indicate if the value is out of specification.", :created_at => nil, :updated_at => "2012-10-09 02:32:03", :category_id => 14, :status => "Public", :source_id => nil, :sortOrder => nil }
